import { useRef } from "react";
import { Tabs, Tab } from "react-bootstrap";
import { Link, useLocation, BrowserRouter } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

import "./Topbar.css";

export default function Navbar() {
  const navRef = useRef();
  const location = useLocation();

  const showNavbar = () => {
    navRef.current.classList.toggle("responsive_nav");
  };

  return (
    <header>
      <h3>LOGO</h3>
      <nav ref={navRef}>
        <Tabs
          defaultActiveKey="/home"
          id="navbar-tabs"
          activeKey={location.pathname}
        >
          <Tab eventKey="/home" title={<Link to="/home">Home</Link>} />
          <Tab eventKey="/profile" title={<Link to="/profile">My work</Link>} />
          <Tab eventKey="/contact" title={<Link to="/contact">Blog</Link>} />
          <Tab eventKey="/about" title={<Link to="/about">About me</Link>} />
        </Tabs>
      </nav>
    </header>
  );
}
