import React, { useState } from "react";
import { Tabs, Tab, Nav, Navbar, NavDropdown, NavItem } from "react-bootstrap";

import Typography from "@mui/material/Typography";
import Box from "@mui/material/Box";
import PersonOutlinedIcon from "@mui/icons-material/PersonOutlined";
import CalendarTodayOutlinedIcon from "@mui/icons-material/CalendarTodayOutlined";
import HelpOutlineOutlinedIcon from "@mui/icons-material/HelpOutline";

const themeColors = {
  primary: "#E1242A",
  secondary: "#891515",
  darkGrey: "#34373F",
  lightGrey: "#A1A4AC",
  black: "#000000",
  white: "#FFFFFF",
  background: "#EDF2F4",
};

const Topbar = () => {
  const [selected, setSelected] = useState();

  const Item = ({ title, to, icon }) => (
    <Box
      sx={{
        mr: 3,
        display: "flex",
        alignItems: "center",
        cursor: "pointer",
        borderRadius: "8px",
        padding: "0.5em",
        transition: "all 0.3s",
        ...(selected === title && {
          backgroundColor: themeColors.primary,
          color: themeColors.darkGrey,
        }),
        "&:hover": {
          backgroundColor: themeColors.lightGrey,
        },
      }}
    >
      <Nav.Link
        href={to}
        onClick={() => setSelected(title)}
        className={selected === title ? "active" : ""}
        style={{
          display: "flex",
          alignItems: "center",
          textDecoration: "none",
          color: "inherit",
        }}
      >
        <Box sx={{ mr: 1 }}>{icon}</Box>
        {title}
      </Nav.Link>
    </Box>
  );

  return (
    <Box
      sx={{
        backgroundColor: themeColors.secondary,
        "& .active": { color: themeColors.darkGrey },
        "& a:hover": { textDecoration: "none", color: themeColors.white },
      }}
    >
      <Navbar
        variant="dark"
        expand="lg"
        style={{ backgroundColor: "transparent" }}
      >
        <Navbar.Brand href="/">My Brand</Navbar.Brand>
        <Nav
          className="ml-auto"
          style={{ display: "flex", flexDirection: "row" }}
        >
          <Typography
            variant="h6"
            color={themeColors.lightGrey}
            sx={{ m: "15px 0 5px 20px", display: "inline-block" }}
          >
            Pages
          </Typography>
          <Item title="Profile Form" to="/form" icon={<PersonOutlinedIcon />} />
          <Item
            title="Calendar"
            to="/calendar"
            icon={<CalendarTodayOutlinedIcon />}
          />
          <Item title="FAQ Page" to="/faq" icon={<HelpOutlineOutlinedIcon />} />
        </Nav>
      </Navbar>
    </Box>
  );
};

export default Topbar;
