import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";
import TabbedComponent from "./TabbedComponent";
import ProgressBarComponent from "./ProgressBarComponent";

const MainForm = () => {
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [plant, setPlant] = useState("");
  const [globalBrand, setGlobalBrand] = useState("");

  const [activeTab, setActiveTab] = useState("ERP");

  const handleSubmit = (event) => {
    event.preventDefault();
    // Handle the form submission
  };

  return (
    <div>
      <TabbedComponent activeKey={activeTab} onSelect={setActiveTab} />
      <ProgressBarComponent activeTab={activeTab} />
      <Form onSubmit={handleSubmit}>
        <form onSubmit={handleSubmit}>
          <div>
            <label>
              Review Period
              <input
                type="date"
                value={startDate}
                onChange={(e) => setStartDate(e.target.value)}
              />
              <input
                type="date"
                value={endDate}
                onChange={(e) => setEndDate(e.target.value)}
              />
            </label>
          </div>
          <div>
            <label>
              Plant
              <select value={plant} onChange={(e) => setPlant(e.target.value)}>
                {/* Options will need to be populated here */}
              </select>
            </label>
          </div>
          <div>
            <label>
              Global Brand
              <select
                value={globalBrand}
                onChange={(e) => setGlobalBrand(e.target.value)}
              >
                {/* Options will need to be populated here */}
              </select>
            </label>
          </div>
          <div>
            <button type="button">Back</button>
            <button type="submit">Next</button>
            <button
              type="button"
              onClick={() => {
                /* Reset logic here */
              }}
            >
              Reset
            </button>
          </div>
        </form>
        <Button variant="secondary" type="button">
          Back
        </Button>
        <Button variant="primary" type="submit" className="mx-2">
          Next
        </Button>
        <Button variant="warning" type="reset">
          Reset
        </Button>
      </Form>
    </div>
  );
};

export default MainForm;
