import React, { useState } from "react";

// TabbedComponent
const TabbedComponent = ({ activeTab, setActiveTab }) => {
  const tabs = ["ERP", "TrackWise", "Summary", "Complete"];

  return (
    <div className="tab-bar">
      {tabs.map((tab) => (
        <button
          key={tab}
          onClick={() => setActiveTab(tab)}
          className={activeTab === tab ? "active" : ""}
        >
          {tab}
        </button>
      ))}
    </div>
  );
};

// ProgressBar
const ProgressBar = ({ activeTab }) => {
  const tabIndex = ["ERP", "TrackWise", "Summary", "Complete"].indexOf(
    activeTab
  );
  const progress = ((tabIndex + 1) / 4) * 100;

  return (
    <div className="progress-bar">
      <div className="progress" style={{ width: `${progress}%` }}></div>
    </div>
  );
};

// Form
const Form = ({ activeTab }) => {
  // Add state and form logic here...

  return (
    // Render the form fields based on the active tab
    <form>{/* Form content changes based on activeTab */}</form>
  );
};

// Main component that combines all three
const ReportGenerateForm = () => {
  const [activeTab, setActiveTab] = useState("ERP");

  return (
    <div>
      <TabbedComponent activeTab={activeTab} setActiveTab={setActiveTab} />
      <ProgressBar activeTab={activeTab} />
      <Form activeTab={activeTab} />
    </div>
  );
};

export default ReportGenerateForm;
