import React, { useState, useEffect } from "react";
import { Form, Button, Row, Col } from "react-bootstrap";

export default function StartForm({ handleNavigation }) {
  const [field1, setField1] = useState("");
  const [field2, setField2] = useState("");
  const [field3, setField3] = useState("");
  const [field4, setField4] = useState("");
  const [field5, setField5] = useState("");
  const [field6, setField6] = useState("");

  const data = {
    field1: field1,
    field2: field2,
    field3: ["AA", "BB", "CC"],
    field4: field4,
    field5: field5,
    field6: [123, 234, 345, 456, 567, 678, 789],
  };

  useEffect(() => {
    if (data) {
      console.log(data);
    }
  });

  const handleSubmit = (event) => {
    event.preventDefault();
    // Handle the form submission
  };

  return (
    <div>
      {" "}
      <Form onSubmit={handleSubmit} className="mt-4 form-content">
        <Row className="mb-3">
          <Form.Label column sm={2} className="required">
            Location
          </Form.Label>
          <Col sm={10}>
            <Form.Control
              as="select"
              value={field1}
              onChange={(e) => setField1(e.target.value)}
              required
            ></Form.Control>
          </Col>
        </Row>

        <Row className="mb-3">
          <Form.Label column sm={2}>
            Field 2
          </Form.Label>
          <Col sm={10}>
            <Form.Control
              as="select"
              value={field2}
              onChange={(e) => setField2(e.target.value)}
            ></Form.Control>
          </Col>
        </Row>

        <Row className="form-buttons-container mt-4">
          <Col sm={10} className="d-flex justify-content-end">
            <Button className="form-button me-2" variant="warning" type="reset">
              Reset
            </Button>
            <Button className="form-button" variant="primary" type="submit">
              Next
            </Button>
          </Col>
        </Row>
      </Form>
    </div>
  );
}
