import React, { useState } from "react";
import {
  Tabs,
  Tab,
  ProgressBar,
  Form,
  Button,
  Row,
  Col,
} from "react-bootstrap";
import TabbedComponent from "./TabbedComponent";
import ProgressBarComponent from "./ProgressBarComponent";

const MainForm = () => {
  const [activeTab, setActiveTab] = useState("ERP");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [plant, setPlant] = useState("");
  const [globalBrand, setGlobalBrand] = useState("");

  // Calculate progress based on activeTab. This is a placeholder for the actual logic.
  const progress = { ERP: 25, TrackWise: 50, Summary: 75, Complete: 100 }[
    activeTab
  ];

  const handleSubmit = (event) => {
    event.preventDefault();
    // Handle the form submission
  };

  return (
    <div className="main-form-container">
      <div className="tabbed-component-container">
        <TabbedComponent activeKey={activeTab} onSelect={setActiveTab} />
      </div>
      <div className="progress-bar-container">
        <ProgressBarComponent activeTab={activeTab} />
      </div>
      <Form onSubmit={handleSubmit} className="mt-4 form-content">
        <Form.Group as={Row} controlId="reviewPeriod">
          <Form.Label column sm={2}>
            Review Period
          </Form.Label>
          <Col sm={5}>
            <Form.Control
              type="date"
              value={startDate}
              onChange={(e) => setStartDate(e.target.value)}
              className="mb-2 mb-sm-0" // Add bottom margin on mobile
            />
          </Col>
          <Col sm={5}>
            <Form.Control
              type="date"
              value={endDate}
              onChange={(e) => setEndDate(e.target.value)}
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row} controlId="plantSelect" className="mt-4">
          <Form.Label column sm={2}>
            Plant
          </Form.Label>
          <Col sm={10}>
            <Form.Control
              as="select"
              value={plant}
              onChange={(e) => setPlant(e.target.value)}
            >
              {/* Populate with options */}
            </Form.Control>
          </Col>
        </Form.Group>

        <Form.Group as={Row} controlId="globalBrandSelect" className="mt-4">
          <Form.Label column sm={2}>
            Global Brand
          </Form.Label>
          <Col sm={10}>
            <Form.Control
              as="select"
              value={globalBrand}
              onChange={(e) => setGlobalBrand(e.target.value)}
            >
              {/* Populate with options */}
            </Form.Control>
          </Col>
        </Form.Group>

        <Form.Group as={Row}>
          <Col sm={{ span: 10, offset: 2 }}>
            <div className="form-buttons-container">
              <Button className="form-button" variant="secondary" type="button">
                Back
              </Button>
              <Button className="form-button" variant="primary" type="submit">
                Next
              </Button>
              <Button className="form-button" variant="warning" type="reset">
                Reset
              </Button>
            </div>
          </Col>
        </Form.Group>
      </Form>
    </div>
  );
};

export default MainForm;
