import React from "react";
import { ProgressBar } from "react-bootstrap";

const ProgressBarComponent = ({ activeTab }) => {
  const tabIndex = ["ERP", "TrackWise", "Summary", "Complete"].indexOf(
    activeTab
  );
  const progress = ((tabIndex + 1) / 4) * 100;

  return (
    <ProgressBar
      now={progress}
      variant="info"
      className="custom-progress-bar"
    />
  );
};

export default ProgressBarComponent;
