import React, { useState, useEffect } from "react";
import { Form, Button, Row, Col } from "react-bootstrap";
import Select from "react-select";

export default function StartForm({ handleNavigation }) {
  const [field1Options, setField1Options] = useState(null);
  const [field2Options, setField2Options] = useState(null);
  const [field3Options, setField3Options] = useState(null);
  const [field4Options, setField4Options] = useState(null);
  const [field5Options, setField5Options] = useState(null);
  const [field6Options, setField6Options] = useState(null);

  const [selectedField1, setSelectedField1] = useState(null);
  const [selectedField2, setSelectedField2] = useState(null);

  const data = {
    field1: ["Option 1", "Option 2", "Option 3"],
    field2: ["Option A", "Option B", "Option C"],
    field3: ["AA", "BB", "CC"],
    field4: "FF",
    field5: "GG",
    field6: [123, 234, 345, 456, 567, 678, 789],
  };

  useEffect(() => {
    if (data) {
      console.log(data);
    }
    // Example data for field1 and field2
    const field1Data = data?.field1;
    const field2Data = setField1Options(
      field1Data.map((item) => ({ value: item, label: item }))
    );
    setField2Options(field2Data.map((item) => ({ value: item, label: item })));
  }, []);

  const handleField1Change = (selectedOption) => {
    setSelectedField1(selectedOption);
  };

  const handleField2Change = (selectedOption) => {
    setSelectedField2(selectedOption);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const formData = {
      field1: selectedField1 ? selectedField1.value : "",
      field2: selectedField2 ? selectedField2.value : "",
      // Handle other fields similarly
    };
    console.log(formData);
    // Handle the form submission
  };

  return (
    <div>
      {" "}
      <Form onSubmit={handleSubmit} className="mt-4 form-content">
        <Row className="mb-3">
          <Form.Label column sm={2} className="required">
            Location
          </Form.Label>
          <Row className="mb-3">
            <Form.Label column sm={2} className="required">
              Location
            </Form.Label>
            <Col sm={10}>
              <Select
                options={field1Options}
                value={selectedField1}
                onChange={handleField1Change}
                isClearable
              />
            </Col>
          </Row>
        </Row>

        <Row className="mb-3">
          <Form.Label column sm={2}>
            Field 2
          </Form.Label>
          <Col sm={10}>
            <Form.Control
              as="select"
              options={field2Options}
              value={selectedField2}
              onChange={(e) => setField2Options(e.target.value)}
            ></Form.Control>
          </Col>
        </Row>

        <Row className="form-buttons-container mt-4">
          <Col sm={10} className="d-flex justify-content-end">
            <Button className="form-button me-2" variant="warning" type="reset">
              Reset
            </Button>
            <Button className="form-button" variant="primary" type="submit">
              Next
            </Button>
          </Col>
        </Row>
      </Form>
    </div>
  );
}
